import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

import APIInvoke from "../../utils/APIInvoke";
import swal from "sweetalert";
import '../auth/login/css/style.css';

const Login = () => {
    const [usuario, setUsuario] = useState({
        email: "",
        contra: "",
    });

    const navegador = useNavigate();

    const alerta = (mensaje, tipo, titulo) => {
        // ... (sin cambios)
    };

    const onChange = (e) => {
        setUsuario({
            ...usuario,
            [e.target.name]: e.target.value,
        });
    };

    const iniciarSesion = async () => {
        const data = {
            email: usuario.email,
            contra: usuario.contra,
        };

        try {
            const response = await APIInvoke.invokePOST("/usuarios/login", data);

            const acceso = response.mensaje;
            let titulo, msg, tipo;

            switch (acceso) {
                case "Ingreso":
                    titulo = "Proceso Exitoso!";
                    msg = "Ingreso exitoso al sistema";
                    tipo = "success";
                    alerta(msg, tipo, titulo);

                    localStorage.setItem("user", response.usuario);
                    break;

                case "Denegado":
                    titulo = "Acceso Denegado";
                    msg = "Usuario o contraseña incorrectos";
                    tipo = "error";
                    alerta(msg, tipo, titulo);
                    break;

                default:
                    // Manejar otros casos o roles aquí
                    if (response.rolUsuario === "655ace03854c1a3afc422240") {
                        navegador("/admin");
                    } else if (response.rolUsuario === "655a590e6d60ee6e6679f989") {
                        navegador("/cliente");
                    }
                    break;
            }

            setUsuario({
                email: "",
                contra: "",
            });
        } catch (error) {
            console.error("Error al iniciar sesión:", error);
        }
    };

    const onSubmit = (e) => {
        e.preventDefault();
        iniciarSesion();
    };

    return (
        <section className="ftco-section">
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-12 col-lg-10">
                        <div className="wrap d-md-flex">
                            <div className="text-wrap p-4 p-lg-5 text-center d-flex align-items-center order-md-last">
                                <div className="text w-100">
                                    <h2>Bienvenido a Mi Dulce Online</h2>
                                    <p>¿No tienes una cuenta?</p>
                                    <Link
                                        to="/registrar"
                                        className="btn btn-white btn-outline-white"
                                    >
                                        Registrate
                                    </Link>
                                </div>
                            </div>
                            <div className="login-wrap p-4 p-lg-5">
                                <div className="d-flex">
                                    <div className="w-100">
                                        <h3 className="mb-4">Iniciar Sesion</h3>
                                    </div>
                                </div>
                                <form onSubmit={onSubmit} className="signin-form">
                                    <div className="form-group mb-3">
                                        <input
                                            type="email"
                                            className="form-control"
                                            placeholder="Correo"
                                            id="email"
                                            name="email"
                                            value={usuario.email}
                                            onChange={onChange}
                                            required
                                        />
                                        <label className="label" htmlFor="email">
                                            Correo
                                        </label>
                                    </div>
                                    <div className="form-group mb-3">
                                        <input
                                            type="password"
                                            className="form-control"
                                            placeholder="Contraseña"
                                            id="contra"
                                            name="contra"
                                            value={usuario.contra}
                                            onChange={onChange}
                                            required
                                        />
                                        <label className="label" htmlFor="contra">
                                            Contraseña
                                        </label>
                                    </div>
                                    <div className="form-group">
                                        <button
                                            type="submit"
                                            className="form-control btn btn-primary submit px-3"
                                        >
                                            Iniciar Sesion
                                        </button>
                                        <Link to="/">Cancelar</Link>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default Login;
