import React from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import APIInvoke from "../../utils/APIInvoke"; 
import { useNavigate } from "react-router-dom";
import swal from "sweetalert";



const Actualizardulce = () => {
    const alerta = (mensaje, tipo, titulo) => {
        swal({
            title: titulo,
            text: mensaje,
            icon: tipo,
            buttons: {
                confirm: {
                    text: 'Aceptar',
                    value: true,
                    visible: true,
                    className: 'btn btn-secondary',
                    closeModal: true,
                },
            },
        });
    };

    const navegador = useNavigate();
    const { id } = useParams();

    const [dulce, setDulce] = useState({
        referencia: '',
        nombre: '',
        cantidad: '',
        precio: '',
        Descripcion: '',
        imagen: '', // Nueva propiedad para almacenar la URL de la imagen
    });

    const { referencia, nombre, cantidad, precio, Descripcion} = dulce;

    const cargarDatos = async () => {
        const response = await APIInvoke.invokeGET('/dulces/find/' + id);
        setDulce(response);
    };

    useEffect(() => {
        document.getElementById('referencia').focus();
        cargarDatos();
    }, []);

    const onChange = (e) => {
        setDulce({
            ...dulce,
            [e.target.name]: e.target.value,
        });
    };

    const handleImagenChange = (e) => {
        // Manejar la carga de la imagen
        const file = e.target.files[0];

        if (file) {
            const reader = new FileReader();

            reader.onloadend = () => {
                setDulce({
                    ...dulce,
                    imagen: reader.result, // Almacenar la URL de la imagen
                });
            };

            reader.readAsDataURL(file);
        }
    };

    const editardulce = async () => {
        const data = {
            referencia: dulce.referencia,
            nombre: dulce.nombre,
            cantidad: dulce.cantidad,
            precio: dulce.precio,
            Descripcion: dulce.Descripcion,
            imagen: dulce.imagen,
        };

        const response = await APIInvoke.invokePUT('/dulces/edit/' + id, data);
        let msj, tipo, titulo;

        if (response.mensaje === 'Se edito el dulce correctamente') {
            msj = 'Dulce editado correctamente';
            tipo = 'success';
            titulo = 'Proceso exitoso';
            alerta(msj, tipo, titulo);

            navegador('/list');
        } else {
            msj = 'No se pudo editar el dulce';
            tipo = 'error';
            titulo = 'Error en el proceso';
            alerta(msj, tipo, titulo);

            navegador('/list');
        }
    };

    const onSubmit = (e) => {
        e.preventDefault();
        editardulce();
    };

    return (
        <div>
            <header id="header" className="header d-flex align-items-center">
                <div className="container-fluid container-xl d-flex align-items-center justify-content-between">
                    <Link to="/admin" className="logo d-flex align-items-center">
                        <h1>Mi Dulce Online<span>.</span></h1>
                    </Link>
                    <nav id="navbar" className="navbar">
                        <ul>
                            <li><Link to="/crear">Agregar Dulce</Link></li>
                            <li><Link to="/list">Listar Dulce</Link></li>
                            <li><Link to="/catalogo">Catalogo de Dulces</Link></li>
                            <li><Link to="/">Cerrar Sesion</Link></li>
                        </ul>
                    </nav>{/* .navbar */}
                </div>
            </header>
            <center> <section className="ftco-section">
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-md-12 col-lg-10">

                            <div className="login-wrap p-4 p-lg-5">
                                <div className="d-flex">
                                    <div className="w-100">
                                        <h3 className="mb-4">Actualiza un dulce</h3>
                                    </div>
                                </div>
                                <form onSubmit={onSubmit} className="signin-form">
                                    <div className="form-group mb-3">
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="referencia"
                                            placeholder="Referencia"
                                            name="referencia"
                                            value={referencia}
                                            onChange={onChange}
                                            required
                                        />
                                        <label className="label" htmlFor="floatingInput">
                                            Referencia
                                        </label>
                                    </div>
                                    <div className="form-group mb-3">
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="nombre"
                                            placeholder="Nombre"
                                            name="nombre"
                                            value={nombre}
                                            onChange={onChange}
                                            required
                                        />
                                        <label className="label" htmlFor="floatingInput">
                                            Nombre
                                        </label>
                                    </div>
                                    <div className="form-group mb-3">
                                        <input
                                            type="number"
                                            className="form-control"
                                            id="cantidad"
                                            placeholder="Cantidad"
                                            name="cantidad"
                                            value={cantidad}
                                            onChange={onChange}
                                            required
                                        />
                                        <label className="label" htmlFor="floatingInput">
                                            Cantidad
                                        </label>
                                    </div>
                                    <div className="form-group mb-3">
                                        <input
                                            type="number"
                                            className="form-control"
                                            id="precio"
                                            placeholder="Precio"
                                            name="precio"
                                            value={precio}
                                            onChange={onChange}
                                            required
                                        />
                                        <label className="label" htmlFor="floatingInput">
                                            Precio
                                        </label>
                                    </div>
                                    <div className="form-group mb-3">
