import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import APIInvoke from "../../utils/APIInvoke";
import swal from "sweetalert";

const Login = () => {
    const alerta = (mensaje, tipo, titulo) => {
        swal({
            title: titulo,
            text: mensaje,
            icon: tipo,
            buttons: {
                confirm: {
                    text: "Aceptar",
                    value: true,
                    visible: true,
                    className: "btn btn-secondary",
                    closeModal: true,
                },
            },
        });
    };

    const navegador = useNavigate();

    const [usuario, setUsuario] = useState({
        email: "",
        contra: "",
    });

    const { email, contra } = usuario;

    const onChange = (e) => {
        setUsuario({
            ...usuario,
            [e.target.name]: e.target.value,
        });
    };

    useEffect(() => {
        document.getElementById("email").focus();
    }, []);

    const iniciarSesion = async () => {
        try {
            const response = await APIInvoke.invokePOST("/usuarios/login", usuario);

            const acceso = response.mensaje;

            switch (acceso) {
                case "Ingreso":
                    alerta("Ingreso exitoso al sistema", "success", "Proceso Exitoso!");

                    localStorage.setItem("user", JSON.stringify(response.usuario));
                    localStorage.setItem("token", response.token);

                    // Utiliza navigate desde el objeto navegador
                    const usuario = response.usuario;
                    if (usuario.rol === "admin") {
                        navegador("/admin");
                    } else if (usuario.rol === "cliente") {
                        navegador("/cliente");
                    }
                    break;

                case "Denegado":
                    alerta("Usuario o contraseña incorrectos", "error", "Acceso Denegado");
                    break;

                default:
                    // Otro manejo de casos si es necesario
            }

            setUsuario({
                email: "",
                contra: "",
            });
        } catch (error) {
            console.error("Error al iniciar sesión:", error);
        }
    };

    const onSubmit = (e) => {
        e.preventDefault();
        iniciarSesion();
    };

    return (
        <div className="container">
            <div className="row mt-5">
                <div className="col"></div>
                <div className="col-5">
                    <div className="card text-center">
                        <div className="card-header">Acceso al sistema</div>
                        <div className="card-body">
                            <form onSubmit={onSubmit}>
                                <div>
                                    <div className="form-floating mb-3">
                                        <input
                                            type="email"
                                            className="form-control"
                                            placeholder="name@example.com"
                                            id="email"
                                            name="email"
                                            value={email}
                                            onChange={onChange}
                                            required
                                        />
                                        <label htmlFor="floatingInput">Correo Electrónico</label>
                                    </div>
                                    <div className="form-floating">
                                        <input
                                            type="password"
                                            className="form-control"
                                            placeholder="Contraseña"
                                            id="contra"
                                            name="contra"
                                            value={contra}
                                            onChange={onChange}
                                            required
                                        />
                                        <label htmlFor="floatingPassword">Contraseña</label>
                                    </div>
                                </div>
                                <div className="container mt-4">
                                    <button type="submit" className="btn btn-primary my-2">
                                        Iniciar Sesión
                                    </button>
                                    <Link to={"/CrearCuenta"} className="btn btn-secondary mx-2">
                                        Crear Cuenta
                                    </Link>
                                </div>
                            </form>
                        </div>
                        <div className="card-footer text-muted">
                            ¿Olvidaste tu contraseña?
                        </div>
                    </div>
                </div>
                <div className="col"></div>
            </div>
        </div>
    );
};

export default Login;
