import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import APIInvoke from "../../utils/APIInvoke";
import swal from "sweetalert";
import '../auth/login/css/style.css';

const Login = () => {
    const alerta = (mensaje, tipo, titulo) => {
        swal({
            title: titulo,
            text: mensaje,
            icon: tipo,
            buttons: {
                confirm: {
                    text: "Aceptar",
                    value: true,
                    visible: true,
                    className: "btn btn-secondary",
                    closeModal: true,
                },
            },
        });
    };

    const navegador = useNavigate();

    const [usuario, setUsuario] = useState({
        email: "",
        contra: "",
    });

    const { email, contra } = usuario;

    const onChange = (e) => {
        setUsuario({
            ...usuario,
            [e.target.name]: e.target.value,
        });
    };

    useEffect(() => {
        document.getElementById("email").focus();
    }, []);

    const iniciarSesion = async () => {
        const body = {
          usuarioAcceso: login.usu,
          claveAcceso: login.cla,
        };
        const response = await APIInvoke.invokePOST(`/api/usuarios/login`, body);
        if (response.ok === "NO_EXISTE") {
          mensajeConfirmacion("error", response.msg);
        } else if (response.ok === "CLAVE_ERRONEA") {
          mensajeConfirmacion("error", response.msg);
        } else {
          localStorage.removeItem("token");
          localStorage.removeItem("iduser");
          localStorage.removeItem("username");
          localStorage.removeItem("rolusuario");
    
          const token = response.tokenJwt;
          const idUsuario = response._id;
          const nombreUsuario = response.nombresUsuario;
          const rolUsuario = response.idRol;
    
          localStorage.setItem("token", token);
          localStorage.setItem("iduser", idUsuario);
          localStorage.setItem("username", nombreUsuario);
          localStorage.setItem("rol", rolUsuario);
    
          if (rolUsuario === "655a591a6d60ee6e6679f98f") {
            navigate("/menu-principal");
          } else if (rolUsuario === "655a590e6d60ee6e6679f989") {
            navigate("/menu-cliente");
          }else{
            navigate("/menu-empleado")
          }
        }
      };

    return (
        <section className="ftco-section">
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-12 col-lg-10">
                        <div className="wrap d-md-flex">
                            <div className="text-wrap p-4 p-lg-5 text-center d-flex align-items-center order-md-last">
                                <div className="text w-100">
                                    <h2>Bienvenido a Mi Dulce Online</h2>
                                    <p>¿No tienes una cuenta?</p>
                                    <Link
                                        to="/registrar"
                                        className="btn btn-white btn-outline-white"
                                    >
                                        Registrate
                                    </Link>
                                </div>
                            </div>
                            <div className="login-wrap p-4 p-lg-5">
                                <div className="d-flex">
                                    <div className="w-100">
                                        <h3 className="mb-4">Iniciar Sesion</h3>
                                    </div>
                                </div>
                                <form
                                    onSubmit={onSubmit}
                                    className="signin-form"
                                >
                                    <div className="form-group mb-3">
                                        <input
                                            type="email"
                                            className="form-control"
                                            placeholder="Correo"
                                            id="email"
                                            name="email"
                                            value={email}
                                            onChange={onChange}
                                            required
                                        />
                                        <label
                                            className="label"
                                            htmlFor="floatingInput"
                                        >
                                            Correo
                                        </label>
                                    </div>
                                    <div className="form-group mb-3">
                                        <input
                                            type="password"
                                            className="form-control"
                                            placeholder="Contraseña"
                                            id="contra"
                                            name="contra"
                                            value={contra}
                                            onChange={onChange}
                                            required
                                        />
                                        <label
                                            className="label"
                                            htmlFor="floatingPassword"
                                        >
                                            Contraseña
                                        </label>
                                    </div>
                                    <div className="form-group">
                                        <button
                                            type="submit"
                                            className="form-control btn btn-primary submit px-3"
                                        >
                                            Iniciar Sesion
                                        </button>
                                        <Link to="/">Cancelar</Link>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default Login;
